import { OrbitControls} from 'three/addons/controls/OrbitControls';
import { GLTFLoader } from 'three/addons/loaders/GLTFLoader';
import { OBJLoader } from 'three/addons/loaders/OBJLoader';
import { MTLLoader } from 'three/addons/loaders/MTLLoader';
import * as THREE from 'three';

export default class ViewObject{
    constructor(canvasRef) {
        this.renderer = new THREE.WebGLRenderer({
            canvas: canvasRef,
            antialias: false,
            alpha: true,
        });
        this.renderer.setPixelRatio( window.devicePixelRatio );
        this.scene = new THREE.Scene();
        this.camera = new THREE.PerspectiveCamera( 75, 1, 0.1, 100 );
        this.camera.position.set(0, 0, 3);

        const ambientLight = new THREE.AmbientLight(0xffffff, 0.5);
        ambientLight.castShadow = true;
        this.scene.add(ambientLight);

        const spotLight = new THREE.SpotLight(0xffffff, 1);
        spotLight.castShadow = true;
        spotLight.position.set(0, 64, 32);
        this.scene.add(spotLight);

        // const loader = new OBJLoader();

        // loader.load('\\Objects\\from-the-chubby-kitchen\\source\\sketchfab\\2\\kitchen.obj', ( Object ) => {
        //     this.scene.add( Object );
        // }, undefined, function (error) {
        //     console.error(error);
        // });

        const mtl_loader = new THREE.MTLLoader();    
        mtl_loader.setTexturePath('\\Objects\\from-the-chubby-kitchen\\textures');
        mtl_loader.setPath('\\Objects\\from-the-chubby-kitchen\\source\\sketchfab\\2');
        mtl_loader.load('kitchen.mtl', function(materials) {

        // Add this (see link below for more detail)
        materials.preload()

        const obj_loader = new THREE.OBJLoader();
        obj_loader.setMaterials(materials);
        obj_loader.setPath('\\Objects\\from-the-chubby-kitchen\\source\\sketchfab\\2');
        obj_loader.load('kitchen.obj', function(object) {

            scene.add(object);  
        })
        });

        const controls = new OrbitControls(this.camera,this.renderer.domElement);

        this.renderer.setSize(720, 720);

    

        this.update();
    }

    // ******************* PUBLIC EVENTS ******************* //
    updateValue(value) {
      // Whatever you need to do with React props
    }

    onMouseMove() {
      // Mouse moves
    }

    onWindowResize(vpW, vpH) {
        // this.renderer.setSize(vpW, vpH);
    }

    // ******************* RENDER LOOP ******************* //
    update(t) {
        this.renderer.render(this.scene, this.camera);

        requestAnimationFrame(this.update.bind(this));
    }
}
